import React, { useState } from "react";
import "../styles/product.css";

function Product(props) {
  const { id, image, name, descrip, price } = props;

  // Add state to keep track of hover state
  const [isHovered, setIsHovered] = useState(false);

  const [itemAdded, setItemAdded] = useState(false)
  console.log(itemAdded)

  function addToCart() {
    // Update the state to indicate that the item has been added to the cart
    setAddedToCart(true);

    props.continueToPass({
      id: id,
      key: id,
      image: image,
      name: name,
      descrip: descrip,
      price: price,
      qty: 1,
    });
  }

  return (
    <div className="product-container">
      <div
        className="product-img-container"
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        <img
          className="product-details product-img"
          src={image}
          alt={name}
          style={{
            transform: isHovered ? "scale(1.1)" : "scale(1)",
          }}
        />
      </div>
      <h3 className="product-details product-name">{name}</h3>
      <h5 className="product-details product-description">{descrip}</h5>
      <h3 className="product-details product-price">{"$" + price}</h3>

      {/* Conditional rendering based on whether the item is added to the cart */}
      {addedToCart ? (
        <span className="product-details added-to-cart">Added to cart</span>
      ) : (
        <button
          onClick={addToCart}
          className="btn btn-secondary product-details addBtn"
        >
          Buy
        </button>
      )}
    </div>
  );

}

export default Product;
